import React, { Component } from 'react'
import AuthenticationService from './AuthenticationService.js'
import PostData from './MockJSONdata/MockUsers.json'


class LoginComponent extends Component {

    constructor(props) {
        super(props)

        this.state = {
            username: '',
            password: '',
            hasLoginFailed: false,
            showSuccessMessage: false
        }


        this.handleUsernameChange = this.handleUsernameChange.bind(this)
        this.handlePasswordChange = this.handlePasswordChange.bind(this)
        this.handleChange = this.handleChange.bind(this)
        this.loginClicked = this.loginClicked.bind(this)
        this.registrationClicked = this.registrationClicked.bind(this)
        this.forgotPasswordClicked = this.forgotPasswordClicked.bind(this)
    }



    handleChange(event) {
        console.log(this.state);
        this.setState(
            {
                [event.target.name]
                    : event.target.value
            }
        )
    }


    handleUsernameChange(event) {
        console.log(event.target.name);
        this.setState(
            {
                [event.target.name]
                  :event.target.value
            }
        )
    }

    handlePasswordChange(event) {
        console.log(event.target.value);
        this.setState({password:event.target.value})
    }

    validateUser(userName, passwrd) {
        if(this.state.username===userName && this.state.password===passwrd){
            return true
        }
    }

    loginClicked(a) {

        if(a === true){
            AuthenticationService.registerSuccessfulLogin(this.state.username,this.state.password)
            this.props.history.push(`/welcome/${this.state.username}`)
            this.setState({showSuccessMessage:true})
            this.setState({hasLoginFailed:false})
        }
        else {
            this.setState({showSuccessMessage:false})
            this.setState({hasLoginFailed:true})
        }

        AuthenticationService
        .executeBasicAuthenticationService(this.state.username, this.state.password)
        .then(() => {
            AuthenticationService.registerSuccessfulLogin(this.state.username,this.state.password)
            this.props.history.push(`/welcome/${this.state.username}`)
        }).catch( () =>{
            this.setState({showSuccessMessage:false})
            this.setState({hasLoginFailed:true})
        })
        AuthenticationService
            .executeJwtAuthenticationService(this.state.username, this.state.password)
            .then((response) => {
                AuthenticationService.registerSuccessfulLoginForJwt(this.state.username, response.data.token)
                this.props.history.push(`/welcome/${this.state.username}`)
            }).catch(() => {
                this.setState({ showSuccessMessage: false })
                this.setState({ hasLoginFailed: true })
            })

    }

    // if user clicks on registration button, then switch to registration page
    registrationClicked() {
        this.props.history.push(`/register/`)
    }

    // if user clicks on forgotPassword button, then switch to forgotPassword page
    forgotPasswordClicked() {
        this.props.history.push(`/forgotpassword/`)
    }


    render() {
        return (
            <div>
                {PostData.map((users, index)=>{
                return (this.validateUser(users.userName, users.password))
            })}
                <h1>Login</h1>
                <div className="container">
                    {/*<ShowInvalidCredentials hasLoginFailed={this.state.hasLoginFailed}/>*/}
                    {this.state.hasLoginFailed && <div className="alert alert-warning">Invalid Credentials or something is wrong</div>}
                    {this.state.showSuccessMessage && <div>Login Sucessful</div>}
                    {/* <ShowLoginSuccessMessage showSuccessMessage={this.state.showSuccessMessage}/> */}
                    User Name: <input type="text" name="username" value={this.state.username} onChange={this.handleChange} />
                    Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange} />
                    <button className="btn btn-success" onClick={this.loginClicked(this.validateUser(users.userName, users.password))} >Login</button>
                    <br></br>
                    <button className="btn btn-register" onClick={this.registrationClicked}>Registration</button>
                    <br></br>
                    <button className="btn btn-register" onClick={this.forgotPasswordClicked}>Forgot Password</button>

                </div>
            </div>
        )
    }
}

export default LoginComponent
